if (Meteor.isClient) {
var r = new XMLHttpRequest();
r.open("GET", "https://omgvamp-hearthstone-v1.p.mashape.com/cards", true);
r.onreadystatechange = function () {
  if (r.readyState != 4 || r.status != 200) return;
  console.log("Success: " , r.responseText.Success.Basic);
  var div = document.getElementById('div');
  for (var i = r.responseText.Basic.length - 1; i >= 0; i--) {
  	div.innerHTML = div.innerHTML + "<img src=' " + (r.responseText.Success.Basic[i].img) + "'>";
  };
};
r.setRequestHeader("X-Mashape-Key", "5M5za2r26Umsh5IcTFstcBnN25Zlp1hgRnJjsnOnw7fBHjLyCf");
r.send("banana=yellow");
