if (Meteor.isClient) {
var r = new XMLHttpRequest();
r.open("GET", "https://omgvamp-hearthstone-v1.p.mashape.com/cards", true);
r.onreadystatechange = function () {
  if (r.readyState != 4 || r.status != 200) return;
  var api = r.responseText;
  var jsonResponse = JSON.parse(api);
  console.log("Success: " , jsonResponse);
  var div = document.getElementById('div');
  var cardType = ["Basic", "Classic", "Blackrock Mountain", "Goblins vs Gnomes", "Naxxramas", "The Grand Tournament", "The League of Explorers"]
  for (var i = cardType - 1; i >= 0; i--) {
	  for (var i = jsonResponse.i.length - 1; i >= 0; i--) {
	  	div.innerHTML = div.innerHTML + "<img src=' " + (jsonResponse.Basic[i].img) + "'>";
	  };
   };
};
r.setRequestHeader("X-Mashape-Key", "5M5za2r26Umsh5IcTFstcBnN25Zlp1hgRnJjsnOnw7fBHjLyCf");
r.send("banana=yellow");
